import React from 'react';

function Text() {
    const client = "design";
    let text = 'Programming and design are two essential skills for creating software applications. Programming involves writing code in a specific language that instructs the computer how to perform tasks. Design involves planning the structure, logic, and appearance of the program before coding it. A good design can make a program more understandable, flexible, and maintainable. There are various tools and methods that can help programmers design their programs, such as flowcharts, pseudocode, and CASE software. Programming and design require an agile mindset and the willingness to iterate and improve'

    if (client === "Programming") {
        text = text.split(" ").map((ele) => ele === client ? ele.toLocaleUpperCase() : ele).join(" ");
    } else if (client === "design") {
        text = text.split(" ").map((ele) => ele === client ? ele.toLocaleUpperCase() : ele).join(" ");
    }

    return (
        <p className='mt-3 px-2'>{text}</p>
    );
};

export default Text;
